import './App.css';
import React, { useRef } from 'react';
import { javascript } from '@codemirror/lang-javascript';
import { useState, useEffect } from 'react';
import { io } from "socket.io-client";
import { Socket } from 'engine.io-client';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom'
import {v4 as uuidV4} from 'uuid'
import CodeEditor from './CodeEditor';


function App() {
  const [socket, setSocket] = useState()

  useEffect(() => {
    const s = io("http://localhost:3001");
    setSocket(s)
    return () => {
      s.disconnect();
    }
  }, [])


  const [code, setCode] = useState("Console.log(\"Hello World\")");

  const onChange = React.useCallback((value, viewUpdate) => {
    setCode(value);
    socket.emit("send-changes", value);
    }, [socket]); 



  useEffect(() => {
    if (socket== null) return 

    socket.on("receive-changes", (value)=>{
      setCode(value)  
    });

    return () => {

    }
  }, [socket])



  const html_ref = useRef();


  function Run(){
    useEffect(() => {
      console.log(document.getElementById("html-code"))
      let htmlCode = document.getElementById("html-code").value;
      let cssCode = document.getElementById("css-code").value;
      let jsCode = document.getElementById("js-code").value;
      let output = document.getElementById("output");

      output.contentDocument.body.innerHTML = htmlCode + "<style>"+ cssCode +"</style>";
      output.contentWindow.eval(jsCode);

      return () => {

      }
    }, [])

  }


  return (
    <Router>
      <Routes>
        <Route 
          path='/'
          element={<Navigate replace to={`/project/${uuidV4()}`} />} />
        <Route path='/project/:id' element={
          <div>
            <div className="container">
              <div className="left">
                <label><i className="fa-brands fa-html5"></i>HTML</label>
                <textarea id="html-code" onKeyUp={Run()} ></textarea>

                <label><i className="fa-brands fa-css3-alt"></i>CSS</label>
                {/* <textarea id="css-code" onkeyup={run()}></textarea> */}

                <label><i className="fa-brands fa-js"></i>JavaScript</label>
                {/* <textarea id="js-code" onkeyup={run()}></textarea> */}
              </div>
              <div className="right">
                <label><i className="fa-solid fa-play"></i>Output</label>
                <iframe id="output"></iframe>
              </div>
            </div>
            <CodeEditor lang="javascript" theme="light" onChange={(e) => setCode(e.target.value)} value={code} />
          </div>


        } />
      </Routes>
    </Router>
  );

}

export default App;
