import React from 'react'
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
function Editor(props) {
  const { id: documentId } = useParams();
  const [code, setCode] = useState();

  const socket = props.socket;
  const SAVE_INTERVAL_MS = 2000;
  // load data
  useEffect(() => {
    if (socket == null | code == null) return
    
    socket.once("load-code", code =>{
      // setCode(code)
    })
    
    socket.emit("get-code", documentId)
    
    return () => {
    }
  }, [socket,code,documentId])
  

// receiver
  useEffect(() => {
    if (socket == null | code == null) return
    const handler = (value) => {
      setCode(value);
    }
    socket.on("receive-changes-" + props.lan, handler);

    return () => {
      socket.off("receive-changes-" + props.lan);
    }
  }, [socket, code])

// save code
  useEffect(() => {
    if (socket == null | code == null) return

    const interval = setInterval(()=>{
      socket.emit('save-code', code)
    }, SAVE_INTERVAL_MS)
  
    return () => {
      clearInterval(interval)
    }
  }, [socket,code])
  


  const onChange = React.useCallback((event) => {
    setCode(event.target.value);
    props.onChange(event.target.value);
    socket.emit("send-changes-" + props.lan, event.target.value);
    console.log("send change")
  }, [socket]);

  return (
    <textarea id={props.lan} onChange={onChange} value={code}></textarea>
  )
}

export default Editor
