import React, { useRef, useEffect } from 'react'
import CodeMirror from '@uiw/react-codemirror';
import { javascript } from '@codemirror/lang-javascript';
import {Y as yjs} from 'yjs'
function CodeEditor(props) {
    const editorRef = useRef()
    const yDocRef = useRef()
    const yBindingRef = useRef()
    useEffect(() => {
      const editor = editorRef.current.editor
      const ydoc = new Y.Doc();
      const yBinding = new CodemirrorBinding(ydoc, editor, new Y.Map());

      editor.on('changes', (instance, changes) => {
        const ychanges = [];
        changes.forEach(({ text, from, to }) => {
          ychanges.push([{ text, from, to }]);
        });
        yBinding.awareness.setLocalStateField('user', { name: 'User' });
        yBinding.transaction(() => {
          yBinding.type.get('changes').push(ychanges);
        });
      });
  
      yBindingRef.current = yBinding;
      yDocRef.current = ydoc;
      editorRef.current = editor;
  
    
      return () => {
        yBinding.destroy();
        ydoc.destroy();
      }
    }, [third])
    

    return (
        <CodeMirror
            value={props.code}
            height="200px"
            extensions={[javascript({ jsx: true })]}
            onChange={props.onChange}
        />
    )
}

export default CodeEditor
