import React from 'react'
import { useEffect, useParams } from 'react';
function Editor(props) {
    // const { id : documentId} = useParams()
    // console.log(id)
    const [code, setCode] = useState();

    if (props.lan == 'html') {
        setCode('<div class="world">hello world<div>')
    }
    const socket = props.socket
    
    useEffect(() => {
        if (socket == null | code == null) return 
        const handler = (value) => {
          setCode(value);
        }
        socket.on("receive-changes-html", handler);
    
        return () => {
          socket.off("receive-changes-html", handler);
        }
      }, [socket,htmlCode])

      const onChange = React.useCallback((event) => {
        setCode(event.target.value);
        socket.emit("send-changes-js", event.target.value);
      }, [socket]);

  return (
    <textarea id="js-code" onChange={onChange} value={code}></textarea>
  )
}

export default Editor
