import React, { useRef, useEffect } from 'react'
import CodeMirror from '@uiw/react-codemirror';
import { javascript } from '@codemirror/lang-javascript';
import * as Y from 'yjs'
import {CodemirrorBinding} from 'y-codemirror.next'
function CodeEditor(props) {
    const editorRef = useRef()
    const yDocRef = useRef()
    useEffect(() => {
        const editor = editorRef.current.editor
        const ydoc = new Y.Doc();

        ydoc.clientID = Math.floor(Math.random() * 2 ** 31);

        editor.addKeyMap({
            'Ctrl-Z': (cm) => yUndoPlugin(cm.doc),
            'Ctrl-Y': (cm) => yUndoPlugin(cm.doc, true),
        });

        editor.on('changes', (instance, changes) => {
            const ytext = ydoc.getText('codemirror');
            changes.forEach(({ text, from, to }) => {
                ytext.delete(from, to - from);
                ytext.insert(from, text);
            });
        });

    const ySync = ySyncPlugin(editor, ydoc.getText('codemirror'));
    ySync.setOption('retainUndoStack', true);

    yDocRef.current = ydoc;
    editorRef.current = editor;

    return () => {
      ySync.destroy();
      ydoc.destroy();
    };
    
      return () => {
        yBinding.destroy();
        ydoc.destroy();
      }
    }, [])
    

    return (
        <CodeMirror
            value={props.code}
            height="200px"
            extensions={[javascript({ jsx: true })]}
            onChange={props.onChange}
        />
    )
}

export default CodeEditor
