import React, { useRef, useEffect } from 'react'
import { javascript } from '@codemirror/lang-javascript';
import { css } from '@codemirror/lang-css'
import { html } from '@codemirror/lang-html'
import * as Y from 'yjs'
import {EditorState} from "@codemirror/state"
import {EditorView, keymap} from "@codemirror/view"
import { basicSetup } from 'codemirror';
import {githubLight} from '@ddietr/codemirror-themes/github-light'
import {githubDark} from '@ddietr/codemirror-themes/github-dark'


function CodeEditor(props) {
    const editorRef = useRef(null);
    var lang ;
    var theme ;
    if (props.lang == "javascript") {
        lang = javascript();
    }
    else if (props.lang == "html") {
        lang = html();
    }
    else if (props.lang == "css") {
        lang = css();
    }

    if (props.theme == 'light'){
        theme = githubLight;
    }
    else if (props.theme == 'dark') {
        theme = githubDark;
    }

    
    useEffect(() => {
        const parentElement = document.getElementById("editor-container");

        const state = EditorState.create({
            doc: "Hello, world!",
            extensions: [basicSetup,lang,theme]
        });

        const view = new EditorView({
            state,
            parent: parentElement,
            onUpdate: () =>{
                console.log(view.state.changes)
            }
        });

        editorRef.current = view;
        
        return () => {
            editorRef.current?.destroy();
        };
    }, []);

    const handleChange = () => {
        const value = editorRef.current.state.doc.toString();
        console.log("Editor content:", value);
        // update the state of your React component here
      };
    
      useEffect(() => {
        editorRef.current?.setProps({
          dispatchTransaction(transaction) {
            view.update([transaction]);
            handleChange();
          }
        });
      }, []);
      
    return <div id="editor-container" />;

}

export default CodeEditor
