import React from 'react'
import { useEffect, useState, useReducer } from 'react';
import { useParams } from 'react-router-dom';
function Editor(props) {
  const { id: documentId } = useParams();
  const [codedata, setCodeData] = useState({id: null, code: ''});
  const SAVE_INTERVAL_MS =2000;
  
  const initQueue =[{_id: null, client_id:null, code: null},{_id: null, client_id:null, code: null}]
  const reducerQueue = (state,action) => {
    switch (action.type){
      case 'enqueue':
        state.push(action.payload)
        state.shift()
        return state
    }
    throw "unknown condition"
  }

  const [currentSaveData, setCurrentSaveData] = useReducer(reducer, initQueue)


  const socket = props.socket;
  const [loaded, setLoaded] = useState(false)
  // load data
  useEffect(() => {
    if (socket == null | codedata == null |documentId==null) return

    socket.once("load-code-" + props.lan, codedata => {

    })
  
    // only emit when u are the modifying
    if (codedata.client_id == socket.id){
      socket.emit("get-code-" + props.lan, documentId)
      console.log("client emit ")
    }
    return () => {
    }
  }, [socket,codedata, documentId])


  // receiver
  useEffect(() => {
    if (socket == null | codedata == null) return
    const handler = (data) => {
      setCodeData(data);
      props.onChange(data.code);

      console.log(data)
    }
    
    socket.on("receive-changes-" + props.lan, handler);

    return () => {
      socket.off("receive-changes-" + props.lan);
    }
  }, [socket, codedata])

  


  const onChange = React.useCallback((event) => {
    const data = {
      _id: documentId,
      client_id: socket.id,
      code: event.target.value
    }
    setCodeData(data);
    props.onChange(event.target.value);
    socket.emit("send-changes-" + props.lan, data);
    setCurrentSaveData({data})
  }, [socket]);


  useEffect(() => {
    // console.log(currentSaveData[1].code , codedata.code)
    if( currentSaveData == null | socket ==null) return 
    if( currentSaveData.client_id != socket.id) return
    if ( currentSaveData[1] == currentSaveData[0]) return

    const interval = setInterval(() => {
      socket.emit('save-code-'+props.lan, currentSaveData)
      console.log('save-code-'+props.lan, currentSaveData)
    }, SAVE_INTERVAL_MS)
    
    return () =>{
      clearInterval(interval)
    }
  }, [socket,currentSaveData])
  
  


  return (
    <textarea id={props.lan} onChange={onChange} value={codedata.code}></textarea>
  )
}

export default Editor
